import React, { useState, useEffect }  from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { UserContext } from './UserContext.js';

//components
import Header from './components/Header.jsx';
import Map from './components/Map.jsx';

//page
import Home from './pages/Home.jsx';
import Login from './pages/Login.jsx';
import SignUp from './pages/SignUp.jsx';
import Events from './pages/Events.jsx';
import Places from './pages/Places.jsx';
import Settings from './pages/Settings.jsx';

import { getLoggedInUser } from './api/user.js';

const App = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const unsubscribe = getLoggedInUser().then((res) => {
      if(res.error)  {
        console.log(res.error) 
      }
      else setUser(res.username);
    })
    .catch((err) => console.log(err));

    return () => unsubscribe;
  }, []);

  return (
    <Router>
      <UserContext.Provider value={{user, setUser}}>
        <Header />
        <Routes>
          <Route exact path="/" element={user ? <Navigate to="/map" /> : <Home />} />
          <Route exact path="/login" element={user ? <Navigate to="/map" /> : <Login />} />
          <Route exact path="/signUp" element={user ? <Navigate to="/map" /> : <SignUp />} />
          <Route exact path="/settings" element={user ? <Settings /> : <Navigate to="/" />} />
          <Route exact path="/map" element={!user ? <Navigate to="/" /> : <Map />} />
          <Route exact path="/events" element={<Events />}/>
          <Route exact path="/places" element={<Places />}/>
        </Routes>
      </UserContext.Provider>
    </Router>
  )
}

export default App;
