import React, { Suspense, useState} from 'react'
import { Link, renderMatches, useLocation } from 'react-router-dom';
import {
  MDBContainer,
  MDBRow,
  MDBCol,
} from 'mdb-react-ui-kit';
import { useEffect } from 'react';
function Event (){
    let images=[]
        return (
            <Suspense fallback={<h2>Loading events...</h2>}><LoadEvents/></Suspense>
        );
}

function arrayBufferToBase64(buffer) {
    var binary = '';
    var bytes = [].slice.call(new Uint8Array(buffer));
    bytes.forEach((b) => binary += String.fromCharCode(b));
    return window.btoa(binary);
};

function LoadEvents(){
    const [events,setEvents]=useState([])
    const [filteredEvents,setFilteredEvents]=useState([])
    const [searchField, setSearchField] = useState("");
    const location = useLocation();
    const queryParams = new URLSearchParams(location.search);
    const eventID=queryParams.get('id')
        useEffect(()=>{
            async function fetchData(){
                if(eventID!==null){
                    try{
                        const tmpEvents=[]
                        const response=await fetch('http://localhost:9000/events?id='+eventID)
                        const responseJSON=await response.json()
                        var base64Flag = 'data:image;base64,';
                        var imageStr =arrayBufferToBase64(responseJSON.image.data);
                        var event=responseJSON
                        event.image=base64Flag+imageStr
                        tmpEvents.push(event)
                        setEvents(tmpEvents)
                    }
                    catch{
                        const tmpEvents=[]
                    const response=await fetch('http://localhost:9000/events/')
                    const responseJSON=await response.json()
                    responseJSON.forEach((fetchedEvent)=>{
                        var base64Flag = 'data:image;base64,';
                        var imageStr =arrayBufferToBase64(fetchedEvent.image.data);
                        var event=fetchedEvent
                        event.image=base64Flag+imageStr
                        tmpEvents.push(event)
                    })
                    setEvents(tmpEvents)
                    setFilteredEvents(tmpEvents)
                    }
                }
                else{
                    const tmpEvents=[]
                    const response=await fetch('http://localhost:9000/events/')
                    const responseJSON=await response.json()
                    responseJSON.forEach((fetchedEvent)=>{
                        var base64Flag = 'data:image;base64,';
                        var imageStr =arrayBufferToBase64(fetchedEvent.image.data);
                        var event=fetchedEvent
                        event.image=base64Flag+imageStr
                        tmpEvents.push(event)
                    })
                    setFilteredEvents(tmpEvents)
                    setEvents(tmpEvents)
                }
            }

            fetchData();
        },[])

        let handleChange=t=>{
            setFilteredEvents(events.filter(e=>e.name.toLowerCase().includes(t.target.value.toLowerCase()) || e.description.toLowerCase().includes(t.target.value.toLowerCase())))
        }

        return(
            <div id="events" style={{"overflowY":"scroll","height":"88vh","width":"86vw","margin":"auto"}}>
                <div id="search" style={{"margin-top":"20px"}}>
                <input id="searchBox"
                    className="pa3 bb br3 grow b--none bg-lightest-blue ma3"
                    type = "search" 
                    placeholder = "Search Events"
                    onChange={handleChange}
                    ></input>
                </div>
                {filteredEvents.map(event=>(
                    <div class="event"> 
                        <div key={event.image} style={{"overflowY":"scroll","maxHeight":"94vh","width":"86vw","margin":"auto"}}>
                        <div style={{"display":"block","margin":"30px 0px","borderBottom":"1px solid gray","paddingBottom":"10px"}} key={event.image}>
                    <h2>{event.name}</h2>
                    <h3>{event.description}</h3>
                    <h3>Time: {event.startTime} - {event.endTime}</h3>
                    <h3>Contact: <a href={"tel:"+event.contact}>{event.contact}</a></h3>
                    </div>

                    <div style={{"display":"block","margin-top":"18px","borderBottom":"1px solid gray","paddingBottom":"10px"}}>
                    <img src={event.image} alt="Couldn't load" style={{"display": "block",
                        "maxWidth":"600px",
                        "maxHeight":"30vh",
                        "width": "20vw",
                        "height": "auto"}}/>
                    </div>
                </div>
                    </div>
                ))}
            </div>
        )
}
export default Event
