import { useEffect } from "react";
import L from "leaflet";
import "leaflet-routing-machine/dist/leaflet-routing-machine.css";
import "leaflet-routing-machine";
import { useMap } from "react-leaflet";

L.Marker.prototype.options.icon = L.icon({
  iconUrl: "https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png"
});

export default function Routing({userPosition}, {eventPosition}) {
  const map = useMap();

  const userLat = {userPosition}[0];
  const userLng = {userPosition}[1];
  const evenntLat = {eventPosition}[0];
  const eventLng = {eventPosition}[1];

  useEffect(() => {
    if (!map) return;

    

    const routingControl = L.Routing.control({
      waypoints: [L.latLng(userLat, userLng), 
                  L.latLng(evenntLat, eventLng)],
      routeWhileDragging: true
    }).addTo(map);

    return () => map.removeControl(routingControl);
  }, [map]);

  return null;
}
