export const getLoggedInUser = async ()  => {
    try {
        const res = await axios({
            method: 'GET',
            url:'https://'+process.env.REACT_APP_BASE_URL+'/users/user', 
            credentials: 'include',
        });
        return await res.json();
    } catch (err) {
        throw new Error("Please login to continue");
    }
}
